import React, { Component } from 'react'
import { useState,useEffect } from "react";
const API_BASE="http://localhost:3001";



export default function AddEvent (props) {

  const [newTitle, setNewTitle] = useState("");
  const [newDescription,setNewDescription] = useState("");
  const [newDate, setNewDate] = useState("");
  const [newAdress, setNewAdresss] = useState("");
  const[events, setEvents]=useState([]);

  const addEvent= async() => {
  const data = await fetch(API_BASE+ "/event/new",{
      method:"POST",
      headers: {
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
        title:newTitle,
        description:newDescription,
        date:newDate,
        address:""
      })
    }).then(res=> res.json());
     setEvents([...events,data]);
     setPopupActive(false);
     setNewTitle("");
     setNewDescription("");
     setNewDate("");
  }

    return (
      <div className="popup">
        
   
      
        <div className="content">
        <form onSubmit={addEvent}>
          <h3>Add Event</h3>
         
          <label>upload image: </label><br/><br/>
          <input type="file" id="myFile" name="filename"/><br/><br/>

          <label>Title: </label>
          <input type="text" className="add-event-input" onChange={e=> setNewTitle(e.target.value)} value={newTitle} placeholder="Title" required />
          
          <label>Description: </label>
          <input type="text" className="add-event-input" onChange={e=> setNewDescription(e.target.value)} value={newDescription} placeholder="Adress" required/>
          
          <label>Event Adress: </label>
          <input type="date" className="add-event-input" onChange={e=> setNewAdresss(e.target.value)} value={newAdress} placeholder="Event Adress" required/>
          
          <label>Event Date</label>
          <input type="text" className="add-event-input" onChange={e=> setNewDate(e.target.value)} value={newDate} placeholder="Event Date" required/>
          
          <button type="submit"className="button"  >Creat Event</button>
        </form>
        </div>

      </div>
    )
  
}
