import React, { Component } from 'react'
import SimpleEvent from './SimpleEvent.js'
import './Events.css'
import { useState,useEffect } from "react";
const API_BASE="http://localhost:3001";


export default class Events extends Component {
  constructor(props){
    super(props);
    this.state={}
    
    //const[events, setEvents]=useState([]);

      GetEvents();
     // console.log(events);
   
  
    const GetEvents =()=>{
      fetch(API_BASE+"/events").then(res=>res.json())
                               .then(data=>this.setState({data}))
                               .catch(err=> console.error("Error",err));
  
    }}
  
  render() {

    return (
      <div className='EventsContainer'> 
        
        <h2 className='EventsHeader'>Events :</h2>

        <div className='content'>
{this.state.data.map((e,index)=>(
  <SimpleEvent title={e.title} description={e.description} date={e.date}
  address={e.address} image={e.image} key={index}/>

  ))}
        </div>
      </div>
     
    )
  }
}
