import React, { Component } from 'react'
import SimpleEvent from './SimpleEvent.js'
import './Events.css'
import { useState,useEffect } from "react";
const API_BASE="http://localhost:3001";

export default function Events(props) {



  const addEvent=async() => {
      const data = await fetch(API_BASE+ "/event/new",{
        method:"POST",
        headers: {
          "Content-Type":"application/json"
        },
        body: JSON.stringify({
          title:newTitle,
          description:newDescription,
          date:newDate,
          address:"adress test 2"
        })
      }).then(res=> res.json());
       setEvents([...events,data]);
       setPopupActive(false);
       setNewTitle("");
       setNewDescription("");
       setNewDate("");
    }


    const GetEvents =()=>{
      fetch(API_BASE+"/events").then(res=>res.json())
                               .then(data=>setEvents(data))
                               .catch(err=> console.error("Error",err));}
    const[events, setEvents]=useState([]);
    useEffect(()=>{
      GetEvents();
    },[])
   
    const deleteEvent = async id =>{
        const data = await fetch(API_BASE+"/event/delete/"+id ,{method:"DELETE"})
        .then(res=>res.json());
        setEvents(events=>events.filter(event => event._id == data._id))
        GetEvents();}

    let refs=[]
    const change=(index)=>{

           if (refs.indexOf(index)>=0){
               refs.pop(index);
           }else{
             refs.push(index);
           }}
    return (
      <div className='EventsContainer'> 
        <h2 className='EventsHeader'>Events :</h2>
        <div className='content'>

  {events.map((e)=>(
  <SimpleEvent title={e.title}
   description={e.description} date={e.date}
  address={e.address} image={e.image} id={e._id} call={change} oneDel={deleteEvent}/>
))}
      </div>
      </div>)}
