import React, { Component } from 'react'
import './Events.css'
const API_BASE="http://localhost:3001";

export default class SimpleEvent extends Component {
    constructor(props){
        super(props);
        this.state={
             id:this.props.idN,
             title:this.props.title,
             description:this.props.description,
             date:this.props.date,
             address:this.props.address,
             image:this.props.image
        }   

  }
 
   

  render() {
       return (

        <div className='EventContainer' onClick={() => this.props.call(this.state.id)}>

           <img src={this.state.image} className='EventImage'/>
          <div className='EventInfoContainer'>
                <h3>{this.state.title}</h3>
                <div>{this.state.description}</div>
                <div>{this.state.address}</div> 
                <div>{this.state.date}</div>
          </div>
          <div className="delete-event" onClick={() => this.props.oneDel(this.state.id)}>X</div>

        </div>
    )
  }
}
