import React, { Component } from 'react'
import { useState,useEffect } from "react";
import './AddEvents.css'
const API_BASE="http://localhost:3001";



export default function AddEvent (props) {

  const [newTitle, setNewTitle] = useState("");
  const [newDescription,setNewDescription] = useState("");
  const [newDate, setNewDate] = useState("");
  const [newAdress, setNewAdresss] = useState("");
  const[events, setEvents]=useState([]);

  const addEvent= async() => {
  const data = await fetch(API_BASE+ "/event/new",{
      method:"POST",
      headers: {
        "Content-Type":"application/json"
      },
      body:JSON.stringify({
        title:newTitle,
        description:newDescription,
        date:newDate,
        address:""
      })
    }).then(res=> res.json());
     setEvents([...events,data]);
     setNewTitle("");
     setNewDescription("");
     setNewDate("");
  }

    return (
      <div className='EventsContainer'> 
      <h2 className='EventsHeader'>Add events :</h2>
        <div className="content">

        <form onSubmit={addEvent} action="#" onsubmit="return false">
          <label for="myFile" className='add-event-image-label'>Add an image for your event !</label>
          <input type="file" id="myFile" name="filename" className='hidden' />  
          <input type="text" className="add-event-input" onChange={e=> setNewTitle(e.target.value)} value={newTitle} placeholder="Title" required />
          <input type="text" className="add-event-input" onChange={e=> setNewDescription(e.target.value)} value={newDescription} placeholder="Description" required/>
          <input type="text" className="add-event-input" onChange={e=> setNewDate(e.target.value)} value={newDate} placeholder="Event Adress" required/>
          <input type="date" className="add-event-input" onChange={e=> setNewAdresss(e.target.value)} value={newAdress} placeholder="Event Date" required/>
          <button type="submit"className="add-event-button"  >Add  Event</button>
        </form>
        </div>

      </div>
    )
  
}
